changeSet.sql:
CREATE TABLE IF NOT EXISTS T_USER (
USER_ID varchar(255) not null,
LOGIN varchar(150),
PASSWORD varchar(150),
PRIMARY KEY (USER_ID));
ALTER TABLE T_CLIENT ADD COLUMN USER_ID VARCHAR(255) NULL AFTER SURNAME;
ALTER TABLE T_CLIENT ADD CONSTRAINT fk_t_USER FOREIGN KEY (USER_ID)
REFERENCES T_USER (USER_ID);
